import { useState } from 'react';
import { Modal } from 'react-bootstrap';

function Portfolio({ imageLinks }) {
    const [selectedImage, setSelectedImage] = useState(null);

    const handleImageClick = (index) => {
        setSelectedImage(index);
    };

    const handleClose = () => {
        setSelectedImage(null);
    };

    return (
        <>
            {imageLinks.map((link, index) => (
                <div key={index} className="col-md-4">
                    <img
                        src={link}
                        className="img-fluid rounded portfolio-image"
                        style={{
                            height: "300px",
                            objectFit: "cover",
                            cursor: "pointer",
                            transition: "opacity 0.3s ease-in-out", // Smooth transition for opacity
                        }}
                        onClick={() => handleImageClick(index)}
                        onMouseEnter={(e) => e.target.style.opacity = "0.8"} // Reduce opacity on hover
                        onMouseLeave={(e) => e.target.style.opacity = "1"} // Restore opacity when not hovered
                    />
                </div>
            ))}
            <Modal show={selectedImage !== null} onHide={handleClose}>
                <Modal.Body>
                    <img
                        src={selectedImage !== null ? imageLinks[selectedImage] : ''}
                        alt={`Image ${selectedImage + 1}`}
                        className="img-fluid rounded"
                        style={{ maxHeight: "100vh", width: "500px", margin: "0 auto"}}
                    />
                </Modal.Body>
            </Modal>
        </>
    );
}

export default Portfolio;
