import { useState } from 'react';
import { Modal } from 'react-bootstrap';

function Portfolio({ imageLinks }) {
    const [selectedImage, setSelectedImage] = useState(null);

    const handleImageClick = (index) => {
        setSelectedImage(index);
    };

    const handleClose = () => {
        setSelectedImage(null);
    };

    return (
        <>
            {imageLinks.map((link, index) => (
                <div key={index} className="col-md-4">
                    <img
                        src={link}
                        alt={`Image ${index + 1}`}
                        className="img-fluid rounded portfolio-image"
                        style={{ height: "200px", objectFit: "cover", cursor: "pointer" }}
                        onClick={() => handleImageClick(index)}
                    />
                </div>
            ))}
            <Modal show={selectedImage !== null} onHide={handleClose}>
                <Modal.Body>
                    <img
                        src={selectedImage !== null ? imageLinks[selectedImage] : ''}
                        alt={`Image ${selectedImage + 1}`}
                        className="img-fluid rounded"
                        style={{ maxHeight: "90vh", width: "auto", margin: "0 auto" }}
                    />
                </Modal.Body>
            </Modal>
        </>
    );
}

export default Portfolio;
